import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import './index.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';



class Login extends React.Component {


    constructor(props) {
        super(props)
            this.state = {
                user_name : props.user_name 
            }

            console.log(props.user_name)

    }

    signInUser(e) {

      console.log(e.target.value);
        // axios.post('http://localhost:4000/login/add', obj)
        //     .then(res => console.log(res.data));

    }



    render() {
        const user = this.state;


        if (!user.user_name) {

            return (
                <div>
                    <button type="submit" class="btn btn-danger navbar-btn login-btn " onClick={this.signInUser} >Login</button>
                </div>
            );
        }
        else {
            return (
                <div>
                    Hi test user
            </div>
            );
        }



    }

}

class App extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            user_name: 'Bikash'
        }
    }



    render() {



        return (

            <div class="container-fluid">
                <div className="App">
                    <div class="jumbotron">
                        <div class="navbar-brand">
                            <h1>Hire An Artist</h1>
                            <p class="lead">One stop solution for content creators </p>
                        </div>
                        <div className="float-right">
                            <Login user_name={this.state.user_name} />
                        </div>
                    </div>
                </div>
                <div className="row mt-4">
                    <div className="col-sm">
                        <button type="button" class="btn btn-primary btn-lg  ">Director</button>
                    </div>
                    <div className="col-sm">
                        <button type="button" class="btn btn-success btn-lg ">Screenplay</button>
                    </div>
                    <div className="col-sm">
                        <button type="button" class="btn btn-warning btn-lg ">Cinematographer</button>
                    </div>
                    <div className="col-sm">
                        <button type="button" class="btn btn-info btn-lg ">Editor</button>
                    </div>
                </div>

                <div className="row mt-4" />

                <div className="row mt-4" />

                <div className="row mt-4">
                    <div className="col-sm">
                        <button type="button" class="btn btn-secondary btn-lg ">Sound</button>
                    </div>
                    <div className="col-sm">
                        <button type="button" class="btn btn-dark btn-lg ">Production</button>
                    </div>
                    <div className="col-sm">
                        <button type="button" class="btn btn-danger btn-lg ">Actors</button>
                    </div>
                    <div className="col-sm">
                        <button type="button" class="btn btn-primary btn-lg ">Logistics</button>
                    </div>
                </div>
            </div>
        );
    }
}


ReactDOM.render(
    <App />,
    document.getElementById('root')
);

