    }
    render() {
        const user = this.state;
        console.log(user.user_name)
        if (!user.user_name) {

            return (
                <div>
                    <button type="submit" class="btn btn-danger navbar-btn login-btn " onClick={this.signInUser} >Login</button>
                </div>
            );
        }
        else {
            return (
                <div>
                    Hi {this.state.user_name}
                </div>
            );
        }
    }

}

